// Check if notification should be sent based on user preferences and quiet hours
const notificationRequest = $('Notification Request').first().json;
const userPrefs = $input.first().json;

const now = new Date();
const currentHour = now.getHours();
const currentDay = now.getDay(); // 0 = Sunday, 1 = Monday, etc.

// Default quiet hours: 10 PM to 7 AM
const quietStart = userPrefs.quietHours?.start || 22;
const quietEnd = userPrefs.quietHours?.end || 7;

// Check if current time is in quiet hours
let isQuietTime = false;
if (quietStart > quietEnd) {
  // Quiet hours span midnight (e.g., 10 PM to 7 AM)
  isQuietTime = currentHour >= quietStart || currentHour < quietEnd;
} else {
  // Quiet hours within same day
  isQuietTime = currentHour >= quietStart && currentHour < quietEnd;
}

// Check if notification type is enabled
const notificationType = notificationRequest.type;
const typePrefs = userPrefs.notificationTypes || {};
const isTypeEnabled = typePrefs[notificationType] !== false; // Default to true if not specified

// Check if push notifications are globally enabled
const pushEnabled = userPrefs.pushNotifications !== false;

// Emergency notifications bypass quiet hours
const isEmergency = notificationRequest.priority === 'critical' || 
                   notificationRequest.type === 'physician_alert';

const shouldSend = pushEnabled && isTypeEnabled && (!isQuietTime || isEmergency);

// If notification should be delayed, calculate next available time
let scheduleFor = null;
if (!shouldSend && !isEmergency && isQuietTime) {
  const nextMorning = new Date(now);
  nextMorning.setHours(quietEnd, 0, 0, 0);
  if (nextMorning <= now) {
    nextMorning.setDate(nextMorning.getDate() + 1);
  }
  scheduleFor = nextMorning.toISOString();
}

return {
  ...notificationRequest,
  userPreferences: userPrefs,
  shouldSend: shouldSend,
  isQuietTime: isQuietTime,
  isEmergency: isEmergency,
  scheduleFor: scheduleFor,
  checkTimestamp: now.toISOString()
};

